mapper.select:
select 	
	m.mem_id
	, m.mem_nickname
	, m.mem_savefile
from (
	select t.*, rownum as ord
	from (
		select mem_id, count(*) as orderbest
		from (
			select mem_id
			from CS_BOARD_LIKE
			
	         
		           and boa_id in(
		              
		              #{a}
		              
		          )
	        
	 		
	 		and mem_id!=#{mem_id}
		)
		group by mem_id
		order by orderbest desc
	) t
) sub join CS_MEMBER m
on sub.mem_id=m.mem_id
where sub.ord<=3
