mapper.insert:
insert into
	CS_MEMBER
		(mem_id	
		, mem_pw
		, mem_birth
		, mem_gender
		, mem_nickname
		
		, mem_originalfile
		, mem_savefile
		
		) values (
			#{mem_id}
			, #{mem_pw}
			, #{mem_birth}
			, #{mem_gender}
			, #{mem_nickname}
			
			, #{mem_originalfile}
			, #{mem_savefile}
			
		)

mapper.update:
update CS_MEMBER
	set mem_pw=#{mem_pw}
	, mem_birth=TO_DATE(#{mem_birth}, 'yyMMdd')
	, mem_gender=#{mem_gender}
	, mem_nickname=#{mem_nickname}
	
	, mem_originalfile=#{mem_originalfile}
	, mem_savefile=#{mem_savefile}
	
where
	mem_id = #{mem_id}
and
	MEM_CHECK = 1

update CS_MEMBER
	set mem_leave_date=sysdate
	where mem_id=#{mem_id}
